import React, { useState, useEffect } from "react";
import "../App.css";

const Main = () => {
  const [search, setSearch] = useState("Delhi");
  const [weather, setWeather] = useState(null);
  useEffect(() => {
    let url = `https://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=7e0997fac199ad392f313156c9868d7e`;

    const fetchdata = async () => {
      let response = await fetch(url);
      let data = await response.json();
      console.log(data);
      setWeather(data);
      console.log(weather.main.temp);
      console.log(weather.name);
    };
    fetchdata();
  }, [search]);
  return (
    // <>
    // {!weather.name?
    // "No Data Found"
    // :
    <>
      <div className="container border">
        <div className="inner-container">
          <div className="main">
            <h1 id="heading">Weather Forecasting</h1>
            <div className="input">
              <input
                type="text"
                value={search}
                onChange={(event) => {
                  setSearch(event.target.value);
                }}
              />
            </div>
            {!weather.name ? (
              <p>No Data Found</p>
            ) : (
              <>
                <h1 id="temp">{search}</h1>
                <h1 id="temp">{weather.main.temp}</h1>
                <strong>Max. Temp :5.25°C </strong>
                <strong>Min. Temp:3.89°C </strong>
              </>
            )}
          </div>
        </div>
      </div>
    </>
    // }
    // </>
  );
};

export default Main;
