import React,{useState,useEffect,useRef} from 'react';
import "../App.css";

const Main = () => {
    const [city,setCity]=useState(null);
        const [search,setSearch]=useState("Delhi");
        const[icon,setIcon]=useState(null);
        const[description,setDescription]=useState("");
        const[windspeed,setWindspeed]=useState(null);
        const [humidity,setHumidity]=useState(null);
        
    const name=useRef(null);

      useEffect(()=>{
          let url=`https://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=7e0997fac199ad392f313156c9868d7e`

      const fetchdata= async ()=>{
          let response=await fetch(url);
          let data= await response.json();
          console.log(data);
          console.log(data.weather[0].icon)
          setCity(data.main);;
          setIcon(data.weather[0].icon);
          setDescription(data.main.description);
          setWindspeed(data.wind);
          setHumidity()
          
       
         

      }
  fetchdata();},[search])
  return (
    <>
    <div className="container-fluid">
      <div className="box">
      <h1 className="heading">Weather Forecasting</h1>

        <div className="input-box">
          <input type="search" ref={name} />
          <button type="button"    onClick={(event)=>{event.preventDefault(); setSearch((name.current.value))}}><i class="fa-solid fa-magnifying-glass" ></i></button>
        </div>
        {!city?
        (<p>No Data Found</p>):
        <>
         <div className="info">
          <h2 className="location">{search.toUpperCase()}</h2>
          <h1 className="temp">{city.temp}°C</h1>
         <img src={`http://openweathermap.org/img/w/${icon}.png`} />
          <h3 className="min_max">Min_Temp:{city.temp_min}°C|| Max_Temp:{city.temp_max}°C</h3>
        </div>
          </>
        }
      </div>
      </div>
        </>
  );
};

export default Main;
