import React, { useEffect ,useState} from "react";
import "../App.css";

const Main = () => {
const [location,seLocation]=useState("Delhi");
const [temperature,setTemperature]=useState(null);
const[icon,setIcon]=useState("03d");
const[description,setdescription]=useState("Partly cloud");
const[humcidity,setHumidity]=useState(48);
const [max_temp,setMax_temp]=useState(45);
const [min_temp,setMin_temp]=useState(12);
const [wind,setWind]=useState(67)




let url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=8ada6c990d54ea2fb524366392eb2f00`;
    
const fetchdata = async () => {
  try{
      let response = await fetch(url);
      let data = await response.json();
   
      console.log(data);
    }
    catch(error)
    {
      console.log(error);
    }
}
useEffect(() => {
  fetchdata();
});
  return (
    <>
     <nav className="navbar">
            <h1 className="logo">Weatherly</h1>
          </nav>
         <div className="container">
         
          <div className="search">
            <input type="search" id="search" placeholder="Please Enter Location"/>
            <button className="search-btn"><i className="fa-solid fa-magnifying-glass"></i></button>
          </div>
          <h1 className="location">Delhi</h1>
          <div className="info">
            <img src="https://images.theconversation.com/files/232705/original/file-20180820-30593-1nxanpj.jpg?ixlib=rb-1.1.0&q=45&auto=format&w=1200&h=1200.0&fit=crop" />
            <h1 className="temp">-50 °C</h1>
            <h4 className="description">Partly Cloud</h4>
            </div>
            <div className="rest-info">
              <h4 className="max-temp">Max-temp:57 ℃</h4>
              <h4 className="min-temp">Min-temp: -12 ℃</h4>
              <h4 className="humidity">Humidity : 234</h4>
              <h4 className="windspeed">Wind: 24 m/s</h4>
            </div>
          </div>
        
    </>
  );
}

export default Main
