import React,{useState,useEffect} from 'react';
import "../App.css";

const Main = () => {
    const [city,setCity]=useState(null);
        const [search,setSearch]=useState("Delhi");
      useEffect(()=>{
          let url=`https://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=7e0997fac199ad392f313156c9868d7e`

      const fetchdata= async ()=>{
          let response=await fetch(url);
          let data= await response.json();
          console.log(data);
          setCity(data.main);
         

      }
  fetchdata();},[search])
  return (
    <>
    <div className="container">
      <div className="box">
        <div className="input-box">
          <input type="search" onChange={(event) => {setSearch(event.target.value)}} />
        </div>
        {!city?
        (<p>No Data Found</p>):
        <>
         <div className="info">
          <h2 className="location">{search}</h2>
          <h1 className="temp">{city.temp}°C</h1>
          <h3 className="min_max">Min_tEM:{city.temp_min}°C|| Max:{city.temp_max}°C</h3>
        </div>
          </>
        }
      </div>
      </div>
        </>
  );
};

export default Main;
