import React,{useState,useEffect} from 'react';
import '../App.css';

const Main = () => {
    const [city,setCity]=useState("Delhi");
const [weather,setWeather]=useState(null);
    useEffect(()=>{
        let url=`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=7e0997fac199ad392f313156c9868d7e`
    
    const fetchdata= async ()=>{
        let response=await fetch(url);
        let data= await response.json();
        console.log(data);
        setWeather(data);
    }
fetchdata();},[city])
      return  (
        <>
        {weather.msg==="city not found"?
        "No Data Found"
        :
        <>   
         <div className='container border'>
<div className="inner-container">
    <div className="main">
    <h1 id="heading">Weather Forecasting</h1>
<div className="input">
<input type="text" value={city} onChange={(event)=>{setCity(event.target.value)}} />
</div>
<h1 id="temp">{city}</h1>
<h1 id="temp">9.87°C</h1>
<strong>Max. Temp :5.25°C  </strong><strong>Min. Temp:3.89°C </strong>
</div>
</div>
        </div>
  </>
}
</>

  )
}

export default Main
